#!/bin/bash
# =============================================================================
# setup-web-server.sh
# Projet : Infrastructure Proxmox Cluster avec SDN/NFV
# Auteurs : BERNARDIN Brice / DENA Killian  –  Février 2026
# Serveur : web-server-dmz  |  IP : 10.0.10.10  |  Zone : DMZ (vnet_dmz)
# Stack   : Debian 12 + Apache 2.4 + PHP 8.3
# =============================================================================
set -e

# ─── Couleurs ─────────────────────────────────────────────────────────────────
RED='\033[0;31m'; GREEN='\033[0;32m'; YELLOW='\033[1;33m'; BLUE='\033[0;34m'; NC='\033[0m'
log_info()    { echo -e "${BLUE}[INFO]${NC}    $1"; }
log_success() { echo -e "${GREEN}[OK]${NC}      $1"; }
log_warning() { echo -e "${YELLOW}[WARN]${NC}    $1"; }
log_error()   { echo -e "${RED}[ERROR]${NC}   $1"; exit 1; }

[[ $EUID -ne 0 ]] && log_error "Exécuter en root : sudo bash $0"

echo -e "\n${BLUE}=== INSTALLATION SERVEUR WEB – kilbri / Zone DMZ ===${NC}\n"

# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 1 : Configuration réseau
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 1 : Configuration réseau"

IFACE="ens18"; IP="10.0.10.10"; MASK="255.255.255.0"
GW="10.0.10.1"; DNS="8.8.8.8 1.1.1.1"
HOST="web-server-dmz"; DOMAIN="kilbri.rt-iut.re"

hostnamectl set-hostname "$HOST"
cat > /etc/hosts <<EOF
127.0.0.1       localhost
127.0.1.1       ${HOST}.${DOMAIN} ${HOST}
${IP}           ${HOST}.${DOMAIN} ${HOST}
EOF

cat > /etc/network/interfaces <<EOF
auto lo
iface lo inet loopback

auto ${IFACE}
iface ${IFACE} inet static
    address ${IP}
    netmask ${MASK}
    gateway ${GW}
    dns-nameservers ${DNS}
EOF

systemctl restart networking || true
sleep 2
ping -c1 -W3 "$GW" &>/dev/null && log_success "Passerelle $GW joignable" \
  || log_warning "Passerelle $GW injoignable"

# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 2 : Mise à jour système
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 2 : Mise à jour du système"
apt update -y && apt upgrade -y
apt install -y curl wget gnupg2 lsb-release ca-certificates \
               apt-transport-https software-properties-common net-tools
log_success "Système à jour"

# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 3 : Apache 2.4
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 3 : Installation Apache 2.4"
apt install -y apache2
systemctl enable --now apache2
systemctl is-active --quiet apache2 || log_error "Apache n'a pas démarré"

a2enmod rewrite headers ssl expires deflate proxy proxy_fcgi
a2dissite 000-default.conf 2>/dev/null || true
chown -R www-data:www-data /var/www/html
chmod -R 755 /var/www/html
log_success "Apache 2.4 installé et modules activés"

# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 4 : PHP 8.3 (dépôt Sury)
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 4 : Installation PHP 8.3"
curl -sSLo /usr/share/keyrings/deb.sury.org-php.gpg https://packages.sury.org/php/apt.gpg
echo "deb [signed-by=/usr/share/keyrings/deb.sury.org-php.gpg] \
https://packages.sury.org/php/ $(lsb_release -sc) main" \
  > /etc/apt/sources.list.d/php.list
apt update -y

apt install -y php8.3 php8.3-cli php8.3-fpm php8.3-mysql php8.3-curl \
               php8.3-gd php8.3-mbstring php8.3-xml php8.3-zip php8.3-intl \
               php8.3-bcmath php8.3-opcache libapache2-mod-php8.3

systemctl enable --now php8.3-fpm

# Durcissement php.ini
PHP_INI="/etc/php/8.3/apache2/php.ini"
sed -i 's/^expose_php = On/expose_php = Off/'               "$PHP_INI"
sed -i 's/^display_errors = On/display_errors = Off/'       "$PHP_INI"
sed -i 's/^;date.timezone =/date.timezone = Indian\/Reunion/' "$PHP_INI"
sed -i 's/^upload_max_filesize = .*/upload_max_filesize = 64M/' "$PHP_INI"
sed -i 's/^post_max_size = .*/post_max_size = 64M/'         "$PHP_INI"
sed -i 's/^memory_limit = .*/memory_limit = 256M/'          "$PHP_INI"
sed -i 's/^max_execution_time = .*/max_execution_time = 60/' "$PHP_INI"

a2enmod php8.3
log_success "PHP $(php -r 'echo PHP_VERSION;') installé et configuré"

# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 5 : Virtual Host
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 5 : Configuration Virtual Host"
SITE_DIR="/var/www/kilbri-web"
mkdir -p "$SITE_DIR"
chown -R www-data:www-data "$SITE_DIR"
chmod -R 755 "$SITE_DIR"

cat > /etc/apache2/sites-available/kilbri-web.conf <<VHEOF
<VirtualHost *:80>
    ServerName  web-server-dmz.${DOMAIN}
    ServerAlias ${IP}
    DocumentRoot /var/www/kilbri-web

    <Directory /var/www/kilbri-web>
        Options -Indexes +FollowSymLinks
        AllowOverride All
        Require all granted
    </Directory>

    ErrorLog  \${APACHE_LOG_DIR}/kilbri-web-error.log
    CustomLog \${APACHE_LOG_DIR}/kilbri-web-access.log combined

    Header always set X-Frame-Options        "SAMEORIGIN"
    Header always set X-XSS-Protection       "1; mode=block"
    Header always set X-Content-Type-Options "nosniff"
    Header always set Referrer-Policy        "strict-origin-when-cross-origin"
</VirtualHost>
VHEOF

a2ensite kilbri-web.conf


# Page d'accueil avec DA Réseau Direct
LOGO_B64=""

cat > "$SITE_DIR/index.php" << 'PHPEOF'
<?php
$ip   = $_SERVER['SERVER_ADDR'] ?? 'N/A';
$host = gethostname() ?: 'N/A';
$date = date('d/m/Y H:i:s');
$php  = phpversion();

// Statut des services
function checkPort($host, $port, $timeout = 1) {
    $fp = @fsockopen($host, $port, $errno, $errstr, $timeout);
    if ($fp) { fclose($fp); return true; }
    return false;
}
$db_status  = checkPort('10.0.10.20', 3306) ? 'En ligne' : 'Hors ligne';
$db_class   = checkPort('10.0.10.20', 3306) ? 'status-ok' : 'status-ko';
?>
<!DOCTYPE html>
<html lang="fr">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Réseau Direct – Serveur Web DMZ</title>
  <link rel="preconnect" href="https://fonts.googleapis.com">
  <link href="https://fonts.googleapis.com/css2?family=Exo+2:wght@300;400;600;700&family=Source+Code+Pro:wght@400;600&display=swap" rel="stylesheet">
  <style>
    :root {
      --navy:   #0d2244;
      --blue:   #1a3a6b;
      --green:  #3be06a;
      --green2: #28c957;
      --light:  #e8f0fe;
      --grey:   #8fa3c0;
      --bg:     #07152b;
      --card:   rgba(255,255,255,0.04);
      --border: rgba(59,224,106,0.18);
      --font:   'Exo 2', sans-serif;
      --mono:   'Source Code Pro', monospace;
    }

    *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

    body {
      font-family: var(--font);
      background: var(--bg);
      color: var(--light);
      min-height: 100vh;
      overflow-x: hidden;
    }

    /* ── Animated background grid ── */
    body::before {
      content: '';
      position: fixed; inset: 0; z-index: 0;
      background-image:
        linear-gradient(rgba(59,224,106,.04) 1px, transparent 1px),
        linear-gradient(90deg, rgba(59,224,106,.04) 1px, transparent 1px);
      background-size: 40px 40px;
      animation: gridMove 20s linear infinite;
    }
    @keyframes gridMove { from { background-position: 0 0; } to { background-position: 40px 40px; } }

    /* ── Glowing orbs ── */
    .orb {
      position: fixed; border-radius: 50%; filter: blur(80px);
      pointer-events: none; z-index: 0;
    }
    .orb-1 { width: 500px; height: 500px; background: rgba(26,58,107,.6);  top: -150px; left: -100px; }
    .orb-2 { width: 350px; height: 350px; background: rgba(59,224,106,.12); bottom: -80px; right: -80px; }

    .wrapper {
      position: relative; z-index: 1;
      max-width: 1000px; margin: 0 auto;
      padding: 40px 24px 60px;
    }

    /* ── Header ── */
    header {
      display: flex; align-items: center; gap: 32px;
      padding: 28px 36px;
      background: rgba(13,34,68,.7);
      border: 1px solid var(--border);
      border-radius: 16px;
      backdrop-filter: blur(12px);
      margin-bottom: 36px;
      animation: fadeDown .6s ease both;
    }
    header img { height: 72px; width: auto; flex-shrink: 0; }
    .header-text h1 {
      font-size: 1.55rem; font-weight: 700; color: #fff;
      letter-spacing: -.02em; line-height: 1.2;
    }
    .header-text h1 span { color: var(--green); }
    .header-text p { font-size: .85rem; color: var(--grey); margin-top: 4px; }

    /* ── Status badge ── */
    .badge {
      margin-left: auto; flex-shrink: 0;
      display: flex; align-items: center; gap: 8px;
      background: rgba(59,224,106,.12);
      border: 1px solid rgba(59,224,106,.3);
      border-radius: 40px; padding: 8px 18px;
      font-size: .82rem; font-weight: 600; color: var(--green);
    }
    .badge::before {
      content: ''; width: 8px; height: 8px; border-radius: 50%;
      background: var(--green);
      box-shadow: 0 0 8px var(--green);
      animation: pulse 1.8s ease-in-out infinite;
    }
    @keyframes pulse { 0%,100% { opacity: 1; transform: scale(1); } 50% { opacity: .5; transform: scale(1.4); } }

    /* ── Grid ── */
    .grid { display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin-bottom: 20px; }
    .grid-3 { display: grid; grid-template-columns: repeat(3,1fr); gap: 20px; margin-bottom: 20px; }

    /* ── Cards ── */
    .card {
      background: var(--card);
      border: 1px solid var(--border);
      border-radius: 14px; padding: 24px;
      backdrop-filter: blur(8px);
      animation: fadeUp .5s ease both;
    }
    .card:nth-child(1) { animation-delay: .1s; }
    .card:nth-child(2) { animation-delay: .2s; }
    .card:nth-child(3) { animation-delay: .3s; }

    .card-label {
      font-size: .72rem; font-weight: 600; letter-spacing: .1em;
      text-transform: uppercase; color: var(--green); margin-bottom: 12px;
    }

    /* ── Info rows ── */
    .info-row {
      display: flex; justify-content: space-between; align-items: center;
      padding: 10px 0;
      border-bottom: 1px solid rgba(255,255,255,.05);
      font-size: .88rem;
    }
    .info-row:last-child { border-bottom: none; }
    .info-row .key { color: var(--grey); }
    .info-row .val {
      font-family: var(--mono); font-size: .82rem;
      color: #c8d8f8; text-align: right;
    }
    .info-row .val.green { color: var(--green); }

    /* ── Service cards ── */
    .svc {
      display: flex; flex-direction: column; align-items: center;
      gap: 10px; padding: 20px 16px; text-align: center;
    }
    .svc-icon {
      width: 44px; height: 44px; border-radius: 12px;
      display: flex; align-items: center; justify-content: center;
      font-size: 1.4rem;
      background: rgba(59,224,106,.1);
      border: 1px solid rgba(59,224,106,.2);
    }
    .svc-name { font-size: .82rem; font-weight: 600; color: #c8d8f8; }
    .svc-ver  { font-family: var(--mono); font-size: .75rem; color: var(--grey); }

    .status-ok { color: var(--green); font-weight: 600; font-size: .8rem; }
    .status-ko { color: #f06; font-weight: 600; font-size: .8rem; }

    /* ── Network topology ── */
    .topo {
      font-family: var(--mono); font-size: .8rem;
      line-height: 1.9; color: #c8d8f8;
      padding: 4px 0;
    }
    .topo .node-green { color: var(--green); }
    .topo .node-blue  { color: #6aabf7; }
    .topo .node-grey  { color: var(--grey); }

    /* ── Footer ── */
    footer {
      margin-top: 36px; text-align: center;
      font-size: .78rem; color: var(--grey);
      animation: fadeUp .5s .4s ease both;
    }
    footer a { color: var(--green); text-decoration: none; }
    footer .sep { margin: 0 8px; opacity: .4; }

    @keyframes fadeDown { from { opacity:0; transform:translateY(-16px); } to { opacity:1; transform:none; } }
    @keyframes fadeUp   { from { opacity:0; transform:translateY(16px);  } to { opacity:1; transform:none; } }

    @media (max-width: 640px) {
      .grid, .grid-3 { grid-template-columns: 1fr; }
      header { flex-wrap: wrap; }
      .badge { margin-left: 0; }
    }
  </style>
</head>
<body>
  <div class="orb orb-1"></div>
  <div class="orb orb-2"></div>

  <div class="wrapper">

    <!-- Header -->
    <header>
      <img src="data:image/png;base64,LOGO_PLACEHOLDER" alt="Réseau Direct – Infrastructure IaaS">
      <div class="header-text">
        <h1>Serveur Web – <span>Zone DMZ</span></h1>
        <p>Infrastructure Proxmox Cluster · SDN/NFV · kilbri.rt-iut.re</p>
      </div>
      <div class="badge">Opérationnel</div>
    </header>

    <!-- Infos serveur + réseau -->
    <div class="grid">
      <div class="card">
        <div class="card-label">📡 Serveur</div>
        <div class="info-row"><span class="key">Adresse IP</span><span class="val green"><?= htmlspecialchars($ip) ?></span></div>
        <div class="info-row"><span class="key">Hostname</span><span class="val"><?= htmlspecialchars($host) ?></span></div>
        <div class="info-row"><span class="key">FQDN</span><span class="val">web-server-dmz.kilbri.rt-iut.re</span></div>
        <div class="info-row"><span class="key">Date / Heure</span><span class="val"><?= $date ?></span></div>
        <div class="info-row"><span class="key">Uptime</span><span class="val green"><?= trim(shell_exec('uptime -p') ?: 'N/A') ?></span></div>
      </div>

      <div class="card">
        <div class="card-label">🔗 Réseau SDN</div>
        <div class="topo">
          <span class="node-grey">VXLAN / OPNsense</span><br>
          &nbsp;├─ <span class="node-blue">vnet_dmz</span>&nbsp;&nbsp;&nbsp;10.0.10.0/24<br>
          &nbsp;│&nbsp;&nbsp;&nbsp;├─ <span class="node-green">web-server</span>&nbsp;10.0.10.10<br>
          &nbsp;│&nbsp;&nbsp;&nbsp;└─ <span class="node-<?= (strpos($db_class,'ok') !== false) ? 'green' : 'grey' ?>">db-server</span>&nbsp;&nbsp;10.0.10.20<br>
          &nbsp;├─ <span class="node-blue">vnet_ent</span>&nbsp;&nbsp;&nbsp;10.0.20.0/24<br>
          &nbsp;└─ <span class="node-blue">vnet_admin</span>&nbsp;10.0.30.0/24
        </div>
      </div>
    </div>

    <!-- Services -->
    <div class="grid-3">
      <div class="card svc">
        <div class="svc-icon">🌐</div>
        <div class="svc-name">Apache</div>
        <div class="svc-ver">2.4</div>
        <div class="status-ok">● Actif</div>
      </div>
      <div class="card svc">
        <div class="svc-icon">⚙️</div>
        <div class="svc-name">PHP</div>
        <div class="svc-ver"><?= $php ?></div>
        <div class="status-ok">● Actif</div>
      </div>
      <div class="card svc">
        <div class="svc-icon">🗄️</div>
        <div class="svc-name">MariaDB</div>
        <div class="svc-ver">10.0.10.20:3306</div>
        <div class="<?= $db_class ?>">● <?= $db_status ?></div>
      </div>
    </div>

    <!-- Projet -->
    <div class="card" style="animation-delay:.35s">
      <div class="card-label">🚀 Projet</div>
      <div class="info-row"><span class="key">Projet</span><span class="val">Infrastructure Proxmox Cluster avec SDN/NFV</span></div>
      <div class="info-row"><span class="key">Auteurs</span><span class="val">BERNARDIN Brice · DENA Killian</span></div>
      <div class="info-row"><span class="key">Dépôt</span><span class="val"><a href="https://github.com/Brice97426/script-sae601" style="color:var(--green)">github.com/Brice97426/script-sae601</a></span></div>
      <div class="info-row"><span class="key">IUT</span><span class="val">Université de La Réunion – Département RT</span></div>
    </div>

    <footer>
      <strong style="color:#c8d8f8">Réseau Direct</strong> · Infrastructure IaaS
      <span class="sep">|</span> Proxmox · OPNsense · VXLAN · SDN/NFV
      <span class="sep">|</span> <?= date('Y') ?>
    </footer>

  </div>
</body>
</html>
PHPEOF

# Injecter le logo base64 dans la page
sed -i "s|LOGO_PLACEHOLDER|${LOGO_B64}|g" "$SITE_DIR/index.php"
chown www-data:www-data "$SITE_DIR/index.php"
log_success "Virtual Host + page d'accueil créés avec DA Réseau Direct"


# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 6 : Sécurisation Apache
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 6 : Sécurisation Apache"
SECCONF="/etc/apache2/conf-available/security.conf"
sed -i 's/^ServerTokens .*/ServerTokens Prod/'     "$SECCONF"
sed -i 's/^ServerSignature .*/ServerSignature Off/' "$SECCONF"
a2enconf security
log_success "ServerTokens Prod / ServerSignature Off"

# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 7 : Fail2Ban
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 7 : Installation Fail2Ban"
apt install -y fail2ban
cp /etc/fail2ban/jail.conf /etc/fail2ban/jail.local

cat >> /etc/fail2ban/jail.local <<'F2BEOF'

# ─── kilbri ──────────────────────────────────────────────
[apache-auth]
enabled = true
port    = http,https
logpath = /var/log/apache2/error.log

[apache-badbots]
enabled  = true
port     = http,https
logpath  = /var/log/apache2/access.log
bantime  = 3600
maxretry = 3

[sshd]
enabled  = true
port     = ssh
maxretry = 5
bantime  = 3600
F2BEOF

systemctl enable --now fail2ban
systemctl is-active --quiet fail2ban && log_success "Fail2Ban actif" \
  || log_warning "Fail2Ban non démarré"

# ──────────────────────────────────────────────────────────────────────────────
# ÉTAPE 8 : Redémarrage final + tests
# ──────────────────────────────────────────────────────────────────────────────
log_info "ÉTAPE 8 : Redémarrage final"
systemctl restart apache2 php8.3-fpm

for svc in apache2 php8.3-fpm fail2ban; do
  systemctl is-active --quiet "$svc" && log_success "$svc actif" \
    || log_warning "$svc INACTIF !"
done

sleep 1
CODE=$(curl -s -o /dev/null -w "%{http_code}" http://localhost)
[[ "$CODE" == "200" ]] && log_success "Page web HTTP 200 ✓" \
  || log_warning "Réponse HTTP : $CODE"

# ──────────────────────────────────────────────────────────────────────────────
# RÉSUMÉ
# ──────────────────────────────────────────────────────────────────────────────
echo -e "\n${GREEN}=== ✅ INSTALLATION TERMINÉE – SERVEUR WEB ===${NC}"
echo -e "  URL       : http://${IP}"
echo -e "  FQDN      : http://web-server-dmz.${DOMAIN}"
echo -e "  Webroot   : ${SITE_DIR}"
echo -e "  Logs      : /var/log/apache2/"
echo -e "  PHP       : $(php -r 'echo PHP_VERSION;')"
echo -e "\n${YELLOW}⚠  Post-install :${NC}"
echo "  → Test depuis PC-ENT : curl http://${IP}"
echo "  → Supprimer tout fichier phpinfo après vérification"
echo "  → Configurer SSL si nécessaire : a2enmod ssl"
